import React, { useContext } from 'react';
import { ContainerDefault, TotalBook, Table, THeader, TBody, CartPhoto, Row, RowItem, Button } from './styles';
import { CartContext } from '../../contexts/Cart';


function Cart() {

  const { cart, removeItem } = useContext(CartContext)
  console.log(cart);
  return (
    <>
    {cart.map(item =>
    <ContainerDefault>
        <TotalBook>Total: {item.price}</TotalBook>
        <Table>
          <THeader>
            <th>#</th>
            <th>Nome</th>
            <th>SubTotal</th>
            <th></th>
          </THeader>
          <TBody>
            
              <Row>
                <RowItem>
                  <CartPhoto
                    src={item.image}
                    alt="Foto do livro"
                  />
                </RowItem>
                <RowItem>{item.title}</RowItem>
                <RowItem>{
                  new Intl.NumberFormat(
                    'pt-BR',
                    { style: 'currency', currency: 'BRL' }
                  ).format(item.price)
                }
                </RowItem>
                <RowItem>
                  <Button>Remover</Button>
                </RowItem>
              </Row>
            
          </TBody>
        </Table>
      
    </ContainerDefault>
    )}</>
  )
}

export default Cart;
