import styled from 'styled-components';

export const Container = styled.div`
    width: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
    
`;

export const DivConteudo= styled.div`
    width: 80%;
    height: 640px;
    margin-top 10px;
    border: 1px solid #CCC;
    border-radius: 8px;
    box-shadow: 0 0 1em #CCC;
    display: flex;
    padding: 10px;
`;

export const Image = styled.img`
    width: 50%;
    margin-left: auto;
    margin-right: auto;
    display: block;    
`

export const DivDescription = styled.div`
    display: flex;
    width: 50%;
    background-color: #FFF;
    flex-direction: column;
    
`

export const BookName = styled.h1`
    font-size: 44px;
    color: #0a1a6f;
    margin-bottom: 5px;
    font-weight: bold;
 `
 export const BookPrice = styled.h2`
    font-size: 44px;
    color: black;
    margin-bottom: 5px;
    font-weight: bold;
 `
 export const BookDescription = styled.p`
    line-height: 20px;
    text-align: justify;
    font-size: 20px;
    color: DimGray;
    margin-bottom: 5px;

`
export const ItemButton = styled.button`
    border-radius: 5px;
    background-color: #0a1a6f;
    border: 1px solid #0a1a6f;
    color: #FFF;
    cursor: pointer;     
    height: 30px;
    width: 80px;
    margin-bottom: 5px;
`;
