import styled from 'styled-components';

export const Container = styled.div`
    width: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
`;

export const DivArticle = styled.div`
    width: 80%;
    height: 600px;
    margin-top 10px;
    border: 1px solid #CCC;
    border-radius: 8px;
`;
