import React,{useEffect, useState} from 'react';
import { useParams } from 'react-router-dom';
import { DivConteudo, Container, Image, DivDescription, BookName, BookPrice, BookDescription, ItemButton } from './styles';


function Details() {

  const params = useParams();

  const [details, setDetails] = useState([]);

  useEffect(() => {
    async function handleGetBooks() {
      const response = await fetch(
        `http://localhost:3333/books?id=${params.id}`
      )
      const data = await response.json()
      setDetails(data)
    }
    handleGetBooks() ;
  },[]);
  console.log(params.id);
  return (
    
    <Container>
      <DivConteudo>
        <Image src={details.image} />
        <DivDescription>
          <BookName>{details.title}</BookName>
          <BookPrice>{details.price}</BookPrice>
          <BookDescription>{details.description}</BookDescription>          
          <ItemButton>Adicionar ao carrinho</ItemButton>
        </DivDescription>
      </DivConteudo>
    </Container>
  )
}

export default Details;
