import React, { useContext } from 'react';
import { Table, THeader, TBody, CartPhoto, Row, RowItem, Button } from './styles';
import { CartContext } from '../../contexts/Cart';
import Container from '../../components/Container';

function Cart() {

  const { cart, removeItem } = useContext(CartContext)
  console.log(cart);
  return (
    <Container>
      
        <Table>
          <THeader>
            <th>#</th>
            <th>Nome</th>
            <th>SubTotal</th>
            <th></th>
          </THeader>
          <TBody>
            {cart.map(item =>
              <Row>
                <RowItem>
                  <CartPhoto
                    src={item.image}
                    alt="Foto do livro"
                  />
                </RowItem>
                <RowItem>{item.title}</RowItem>
                <RowItem>{
                  new Intl.NumberFormat(
                    'pt-BR',
                    { style: 'currency', currency: 'BRL' }
                  ).format(item.price)
                }
                </RowItem>
                <RowItem>
                  deletar
                </RowItem>
              </Row>
            )}
          </TBody>
        </Table>
      
    </Container>
  )
}

export default Cart;
