function Cart() {
  const { cart, removeItem } = useContext(CartContext)

  return (
    <Container>      
      <Table>
        <THeader>
          <th>#</th>
          <th>Nome</th>
          <th>Pre√ßo</th>
          <th>Deletar</th>
        </THeader>
        <TBody>
          {cart.map(item =>
            <Row>
              <RowItem>
                <CartPhoto
                  src={item.card_images[0].image_url_small}
                  alt="Foto da carta"
                />
              </RowItem>
              <RowItem>{item.name}</RowItem>
              <RowItem>{
                new Intl.NumberFormat(
                  'pt-BR',
                  { style: 'currency', currency: 'BRL' }
                ).format(item.card_prices[0].cardmarket_price)
              }
              </RowItem>
              <RowItem>
                deletar
              </RowItem>
            </Row>
          )}
        </TBody>
      </Table>
    </Container>
  )
}

export default Cart;
