import "./App.css";
import React,{useState, useEffect} from "react";
import Personagem from "./components/Personagem";
import SeletorPersonagem from "./components/SeletorPersonagem";
import Header from "./components/Header";

const estado_inicial = {
  name: "",
  birth_year: "",
  gender: "",
  mass: "",
  height: "",
  eye_color: "",
  hair_color: "",
};

function App() {
  //https://swapi.dev/api/people/1/

  // useState
  const [personagem, setPersonagem] = useState(estado_inicial);
  useEffect(() => {
    async function fetchPersonagem() {
      let response = await fetch('https://swapi.dev/api/people/1/')
      response = await response.json();
      setPersonagem(response);
    }
    fetchPersonagem();
  }, [])


  // useEffect para alterar título

  // useEffect para alterar personagem

  return (
    <div>      
      <Header />
      <SeletorPersonagem valor={0} onChange={() => {}} />
      {personagem.map(item =>
      <Personagem
        nome={item.name}
        nascimento={item.birth_year}
        genero={item.gender}
        peso={item.mass}
        altura={item.height}
        corOlhos={item.eye_color}
        corCabelo={item.hair_color}
      />
      )}
    </div>
  );
}

export default App;
